import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Container, Row, Col, Image, Button } from 'react-bootstrap'
import { Pencil } from 'react-bootstrap-icons';
import { useAuth0 } from '@auth0/auth0-react';

import '../../styles/account/AccountManager.css';
import UserInfoTable from './UserInfoTable'
import UserAvatar from './UserAvatar'

export default function AccountManager() {
    const [source, setSource] = useState({ source: null })
    const { user } = useAuth0();



    const hiddenFileInput = React.useRef(null);

    const handleFileInputClick = e => {
        hiddenFileInput.current.click();
    }
    const handleFileInputChange = e => {
        const photoUploaded = e.target.files[0];
        const data = new FormData();
        data.append('avatar', photoUploaded)
        data.append('email', user.email)
        putAvatar(data)
    }

    const putAvatar = (data) => {
        var url = "http://localhost:5000/avatar"
        axios.put(url, data, {
            headers: {
              'accept': 'application/json',
              'Accept-Language': 'en-US,en;q=0.8',
              'Content-Type': `multipart/form-data; boundary=${data._boundary}`,
            }
          })
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error.message)
            })
            setTimeout(() => {
                getAvatar()
            }, 2000);
    }

    const getAvatar = () => {
        const time = new Date().getTime();
        var url = "http://localhost:5000/avatar"
        if (user !== undefined) {
            axios.get(url, {
                responseType: 'arraybuffer',
                params: {
                    email: user.email,
                    t: time
                }
            })
                .then(response => {
                    setSource(encodeImage(response.data))
                })
                .catch(error => {
                    console.log(error.message)
                })
        }
    }

    function encodeImage(arrayBuffer) {
        let b64encoded = btoa([].reduce.call(new Uint8Array(arrayBuffer), function (p, c) { return p + String.fromCharCode(c) }, ''))
        let mimetype = "image/png"
        return "data:" + mimetype + ";base64," + b64encoded
    }


    useEffect(() => {
        getAvatar()
    }, [user])


    return (
        <Container className="account_container">
            <Row>
                <UserAvatar />
            </Row>
            <Row>
                <UserInfoTable />
            </Row>
        </Container>

    )
}
