import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Container, Row, Col, Image, Card, Table, Button } from 'react-bootstrap'
import { useHistory } from "react-router-dom";
import { Pencil } from 'react-bootstrap-icons';
import { useAuth0 } from '@auth0/auth0-react'

import '../styles/AccountManager.css'

//ObsÅ‚uga edycji i usuwania konta
export default function AccountManager() {
    const [source, setSource] = useState({ source: null })
    const [userInfo, setUserInfo] = useState()
    const { user } = useAuth0();
    let history = useHistory();

    const getAvatar = () => {
        var url = "http://localhost:5000/getAvatar"
        axios.get(url, { responseType: 'arraybuffer' })
            .then(response => {
                setSource(encodeImage(response.data))
            })
            .catch(error => {
                console.log(error.message)
            })
    }

    const getInfo = () => {
        var url = "http://localhost:5000/getInfo"
        if (user != undefined) {
            axios.post(url, { email: user.email })
                .then(resp => {
                    setUserInfo(resp.data)
                })
        }
    }

    const deleteUser = () => {
        var url = "http://localhost:5000/register"
        print()
        axios.delete(url, { email: user.email })
            .then(response => {
                history.push("/")
            })
            .catch(error => {
                console.log(error.message)
            })
    }

    function encodeImage(arrayBuffer) {
        let b64encoded = btoa([].reduce.call(new Uint8Array(arrayBuffer), function (p, c) { return p + String.fromCharCode(c) }, ''))
        let mimetype = "image/png"
        return "data:" + mimetype + ";base64," + b64encoded
    }

    useEffect(() => {
        getInfo()
    }, [user])

    useEffect(() => {
        getInfo()
        getAvatar()
    }, [])
    return (
        <Container className="account_container">
            <Row>
                <Col className="account_avatar_col">
                    <Image className="avatar_img" src={source} roundedCircle />
                    <Button className="pencil_ico" variant="light" >
                        <Pencil color="brown" size={20} onClick={() => { console.log("hi") }} />
                    </Button>
                </Col>
            </Row>
            <Row>
                <Col>
                    <Card className="text-center profile_card">
                        <Card.Header>Profil</Card.Header>
                        <Card.Body >
                            <Card.Title className="text-left">Twoje informacje</Card.Title>
                            {userInfo &&
                                <Table striped className="text-left">
                                    <tbody>
                                        <tr>
                                            <td>Nazwa uÅ¼ytkownika</td>
                                            <td>{userInfo.nickname}</td>
                                            <td>
                                                <Button className="pencil_table_ico" variant="outline-light">
                                                    <Pencil color="brown" size={20} onClick={() => { console.log("hi") }} />
                                                </Button>
                                            </td>                                        </tr>
                                        <tr>
                                            <td>ImiÄ™ i nazwisko</td>
                                            <td>{userInfo.firstname} {userInfo.surname}</td>
                                            <td>
                                                <Button className="pencil_table_ico" variant="outline-light">
                                                    <Pencil color="brown" size={20} onClick={() => { console.log("hi") }} />
                                                </Button>
                                            </td>                                        </tr>
                                        <tr>
                                            <td>Data urodzenia</td>
                                            <td>{userInfo.dateOfBirth}</td>
                                            <td>
                                                <Button className="pencil_table_ico" variant="outline-light">
                                                    <Pencil color="brown" size={20} onClick={() => { console.log("hi") }} />
                                                </Button>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Adres e-mail</td>
                                            <td colSpan="2">{userInfo.email}</td>
                                        </tr>
                                    </tbody>
                                </Table>
                            }
                        </Card.Body>
                        <Card.Footer>
                            <Button variant="outline-danger" onClick={()=>deleteUser()}>UsuÅ„ konto</Button>
                        </Card.Footer>
                    </Card>
                </Col>
            </Row>
        </Container>
    )
}
