import React from 'react'
import axios from 'axios'
import {useAuth0} from '@auth0/auth0-react'
import { Container, Row,} from 'react-bootstrap'

import '../../styles/account/AccountManager.css';
import UserInfoTable from './UserInfoTable'
import UserAvatar from './UserAvatar'

export default function AccountManager() {
    const { user } = useAuth0();

    const putAvatar = (data) => {
        var url = "http://localhost:5000/avatar"
        axios.put(url, data, {
            headers: {
                'accept': 'application/json',
                'Accept-Language': 'en-US,en;q=0.8',
                'Content-Type': `multipart/form-data; boundary=${data._boundary}`,
            }
        })
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error.message)
            })
        setTimeout(() => {
            getAvatar()
        }, 2000);
    }

    const getAvatar = (setSource) => {
        const time = new Date().getTime();
        var url = "http://localhost:5000/avatar"
        if (user !== undefined) {
            axios.get(url, {
                responseType: 'arraybuffer',
                params: {
                    email: user.email,
                    t: time
                }
            })
                .then(response => {
                    setSource(encodeImage(response.data))
                })
                .catch(error => {
                    console.log(error.message)
                })
        }
    }

    const deleteUser = () => {
        var url = "http://localhost:5000/register"
        axios.delete(url, { data: { email: user.email } })
            .then(response => {
                history.push("/")
            })
            .catch(error => {
                console.log(error.message)
            })
    }

    const getInfo = () => {
        var url = "http://localhost:5000/getInfo"
        if (user !== undefined) {
            axios.get(url, {
                params: {
                    email: user.email
                }
            })
                .then(resp => {
                    setUserInfo(resp.data)
                })
        }
    }

    return (
        <Container className="account_container">
            <Row>
                <UserAvatar />
            </Row>
            <Row>
                <UserInfoTable />
            </Row>
        </Container>

    )
}
