import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { Container, Row, Col, Image, Card, Table } from 'react-bootstrap'
import { Pencil } from 'react-bootstrap-icons';
import { useAuth0 } from '@auth0/auth0-react'

import '../styles/AccountManager.css'

//Zaprojektowanie zarządzania użytkownikiem
export default function AccountManager() {
    const [source, setSource] = useState({ source: null })
    const [userInfo, setUserInfo] = useState()
    const { user } = useAuth0();

    const getAvatar = () => {
        var url = "http://localhost:5000/getAvatar"
        axios.get(url, { responseType: 'arraybuffer' })
            .then(response => {
                setSource(encodeImage(response.data))
            })
    }

    const getInfo = () => { //Do napisania odbiór info
        var url = "http://localhost:5000/getInfo"
        if (user != undefined) {
            axios.post(url, { email: user.email })
                .then(resp => {
                    setUserInfo
                    console.log(resp)
                })
        }
    }

    function encodeImage(arrayBuffer) {
        let b64encoded = btoa([].reduce.call(new Uint8Array(arrayBuffer), function (p, c) { return p + String.fromCharCode(c) }, ''))
        let mimetype = "image/png"
        return "data:" + mimetype + ";base64," + b64encoded
    }

    useEffect(() => {
        getInfo()
    }, [user])

    useEffect(() => {
        getInfo()
        getAvatar()
    }, [])
    return (
        <Container className="account_container">
            <Row>
                <Col className="account_avatar_col">
                    <Image className="avatar_img" src={source} roundedCircle />
                    <Pencil className="pencil_ico" color="brown" size={20} />
                </Col>
            </Row>
            <Row>
                <Col>
                    <Card className="text-center profile_card">
                        <Card.Header>Profil</Card.Header>
                        <Card.Body >
                            <Card.Title className="text-left">Twoje informacje</Card.Title>
                            <Table striped className="text-left">
                                <tbody>
                                    <tr>
                                        <td>Imię i nazwisko</td>
                                        <td>Mark</td>
                                        <td>Otto</td>
                                    </tr>
                                    <tr>
                                        <td>Adres e-mail</td>
                                        <td>Jacob</td>
                                        <td>Thornton</td>
                                    </tr>
                                    <tr>
                                        <td>Data urodzenia</td>
                                        <td>Thornton</td>
                                        <td>Thornton</td>
                                    </tr>
                                </tbody>
                            </Table>
                        </Card.Body>
                        <Card.Footer className="text-muted">2 days ago</Card.Footer>
                    </Card>
                </Col>
            </Row>
        </Container>
    )
}
